import React, { useState, useEffect } from 'react'
import { Badge, VStack, Text, HStack, IconButton, StackDivider, Spacer } from '@chakra-ui/react'
import { FaTrash } from "react-icons/fa";
import { useQuery, useMutation } from '@apollo/client';
import { GET_ALL_TODOS } from '../../Graphql/Queries';
import { DELETE_TODOS } from '../../Graphql/Mutation';
// import { useQuery, useMutation } from "@apollo/client" // use this to query from db

export const TodoList = () => {
    const { data } = useQuery(GET_ALL_TODOS)
    const [deleteTodos, { error, data: data2 }] = useMutation(DELETE_TODOS)
    // const initialTodos = [ 
    //     {
    //         id: 1,
    //         body: "Todo 1 "  
    //     },
    //     {
    //         id: 2,
    //         body: "Todo 2"
    //     },
    // ];
    
    const deleteTodo =async (id: number) =>
    {
        await deleteTodos({
            variables:
            {
                id: id
            }
        })
        console.log(data2)
    }

    // const [todos, setTodos] = useState(initialTodos)

    if(!todos.length)
    {
        return(
            <Badge colorScheme="green" p='5' m='5' borderRadius='lg'>
                No Todos
            </Badge>
        )
    }
    
    return( 
    <div>
        <VStack
        divider={<StackDivider />}
        borderColor='gray.300'
        borderWidth='2px'
        p='4'
        borderRadius='lg'
        width="100%"
        maxW={{ base: '90vw', sm: '100vw', lg: '150vw', xl: '180vw' }}
        alignItems='stretch'
        >
            {data && data.getAllTodos.map((todo: any) =>
            {
                return (
                    <HStack key={todo.id}>
                        <Text>{todo.body}</Text>
                        <Spacer/>
                        <IconButton 
                        aria-label="Delete" 
                        icon={ <FaTrash/>}
                        isRound={true}
                        onClick = {() => { () => { deleteTodo(todo.id) }}}/>
                    </HStack>
                )
            })}
        </VStack>
    </div>
    );
    
}
