import React from 'react'
import { HStack, Text, IconButton, VStack } from '@chakra-ui/react'
import { FaTrash } from "react-icons/fa";
// import { useQuery, useMutation } from "@apollo/client" // use this to query from db

export const TodoList = () => {
    // const { data } = useQuery(GET_ALL_USER)
    const todos = [
        {
            id:1,
            body: "Get Bread",
        },
        {
            id:2,
            body: "Get Butter",
        },
    ]
    return (
        <VStack>
            {todos.map((todo) =>
            {
                <HStack>
                    <Text>{todo.body}</Text>
                    <IconButton 
                    aria-label="Delete" 
                    icon={ <FaTrash/>} 
                    isRound={true} 
                    />
                </HStack>
            })}
        </VStack>
    );
    
}
