import React, { useState, useEffect } from 'react'
import { useMutation } from '@apollo/client';
import {
    HStack,
    Input,
    Button,
    Badge,
  } from "@chakra-ui/react"
import { CREATE_TODO } from '../../Graphql/Mutation';

export const AddTodo = () => {

    const [addTodo, setaddTodo] = useState("")
    const [createTodo, { error, data }] = useMutation(CREATE_TODO)

    const handleSubmit = async (e: any) =>
    {
        e.preventDefault()
        await createTodo({
            variables: {
                body: addTodo
            }
        })

        
        if(!error)
        {   setaddTodo(" ")
            setTimeout(() =>
            {
                return(
                            <Badge colorScheme="green" p='5' m='5' borderRadius='lg'>
                                Sucessfully added to your list
                            </Badge>
                        )
                // window.location.reload()
            }, 3000);
        }
    }

    // if(!error)
    // {
    //     return(
    //         <Badge colorScheme="green" p='5' m='5' borderRadius='lg'>
    //             Sucessfully added to your list
    //         </Badge>
    //     )
    // }

    const AddTodo = (e: string) =>
    {
        //add ke hook
        setaddTodo(e)
    }

    return (
        <HStack mt='10'>
            <Input 
            type="text" 
            variant="filled" 
            placeholder="Todo"
            onChange= {(e) => AddTodo(e.target.value)}
            />
            <Button px="10" colorScheme="blue" onClick= {(event) => handleSubmit(event)}>Add Todo</Button>
        </HStack>
    )
}
