import React from 'react'
import { Button, VStack, Text, HStack, IconButton, StackDivider, Spacer } from '@chakra-ui/react'
import { FaTrash } from "react-icons/fa";
// import { useQuery, useMutation } from "@apollo/client" // use this to query from db

const todos = [ 
    {
        id: 1,
        body: "Todo 1"
    },
    {
        id: 1,
        body: "Todo 1"
    },
];

export const TodoList = () => {
    // const { data } = useQuery(GET_ALL_USER)
    
    
    return( 
    <div>
        <VStack
        divider={<StackDivider />}
        borderColor='gray.300'
        borderWidth='2px'
        p='4'
        borderRadius='lg'
        w="100%"
        maxW={{ base: '90vw', sm: '80vw', lg: '50vw', xl: '40vw' }}
        alignItems='stretch'
        >
            {todos.map((todo) =>
            (
                <HStack key={todo.id}>
                    <Text>{todo.body}</Text>
                    <Spacer/>
                    <IconButton 
                    aria-label="Delete" 
                    icon={ <FaTrash/>}
                    isRound={true}/>
                </HStack>
            ))}
        </VStack>
    </div>
    );
    
}
