import React, { useState, useEffect } from 'react'
import { Button, VStack, Text, HStack, IconButton, StackDivider, Spacer } from '@chakra-ui/react'
import { FaTrash } from "react-icons/fa";
// import { useQuery, useMutation } from "@apollo/client" // use this to query from db

export const TodoList = () => {
    // const { data } = useQuery(GET_ALL_USER)
    const initialTodos = [ 
        {
            id: 1,
            body: "Todo 1 aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa"  
        },
        {
            id: 2,
            body: "Todo 2"
        },
    ];
    
    const deleteTodo = (id: number) =>
    {
        const newTodos = todos.filter((todo) =>
        {
            return todo.id !== id;
        })
        setTodos(newTodos)
    }

    const [todos, setTodos] = useState(initialTodos)
    
    return( 
    <div>
        <VStack
        divider={<StackDivider />}
        borderColor='gray.300'
        borderWidth='2px'
        p='4'
        borderRadius='lg'
        width="100%"
        maxW={{ base: '90vw', sm: '10vw', lg: '500vw', xl: '400vw' }}
        alignItems='stretch'
        >
            {todos.map((todo) =>
            (
                <HStack key={todo.id}>
                    <Text>{todo.body}</Text>
                    <Spacer/>
                    <IconButton 
                    aria-label="Delete" 
                    icon={ <FaTrash/>}
                    isRound={true}
                    onClick = {() => { deleteTodo(todo.id)}}/>
                </HStack>
            ))}
        </VStack>
    </div>
    );
    
}
