import React, { useState, useEffect } from 'react'
import { Badge, VStack, Text, HStack, IconButton, StackDivider, Spacer } from '@chakra-ui/react'
import { FaTrash } from "react-icons/fa";
import { useQuery, useMutation } from '@apollo/client';
import { GET_ALL_TODOS } from '../../Graphql/Queries';
import { DELETE_TODOS, DELETE_USER } from '../../Graphql/Mutation';
// import { useQuery, useMutation } from "@apollo/client" // use this to query from db

export const TodoList = () => {
    const { loading, data } = useQuery(GET_ALL_TODOS)
    const [todos, setTodos] = useState([])

    // const [deleteTodos, { error, data }] = useMutation(DELETE_TODOS)
    const delTodo = useMutation(DELETE_USER)
    // const delData = delTodo.data
    console.log(delTodo)

    useEffect(() => {
        if(data)
        {
            // console.log(data.getAllTodos)
            if (loading) return <p>Loading...</p>;
            setTodos(data.getAllTodos)
            // setTodos(data.getAllTodos[0])
            // console.log(todos)
        }
        console.log(todos)
        
    }, [data])
    
    const deleteTodo =async (id: any) =>
    {
        console.log(id)
        // let parseId = parseInt(id)
        // await deleteTodos({
        //     variables:
        //     {
        //         id: parseId
        //     }
        // })
        // console.log(data)
    }

    // const [todos, setTodos] = useState(initialTodos)

    // if(!todos.length)
    // {
    //     return(
    //         <Badge colorScheme="green" p='5' m='5' borderRadius='lg'>
    //             No Todos
    //         </Badge>
    //     )
    // }
    
    return( 
    <div>
        <VStack
        divider={<StackDivider />}
        borderColor='gray.300'
        borderWidth='2px'
        p='4'
        borderRadius='lg'
        width="100%"
        maxW={{ base: '90vw', sm: '100vw', lg: '150vw', xl: '180vw' }}
        alignItems='stretch'
        >
            {todos.map((todo: any) =>
            {
                return (
                    <HStack key={todo.id}>
                        <Text>{todo.body}</Text>
                        <Spacer/>
                        <IconButton 
                        aria-label="Delete" 
                        icon={ <FaTrash/>}
                        isRound={true}
                        onClick = {() => {  deleteTodo(todo.id) }}/>
                    </HStack>
                )
            })}
            {/* {todos.map((val: any) =>
            {
                return <h1 key= {val.id}>{val.body}</h1>
            })} */}
        </VStack>
    </div>
    );
    
}
