import React from 'react'
import { GET_ALL_USER } from "../Graphql/Queries"
import { useQuery, useMutation } from "@apollo/client"
import { DELETE_USER } from '../Graphql/Mutation'
import {Button} from '@chakra-ui/react'


function ListOfUser() {

    const [deleteThisUser, { error }] = useMutation(DELETE_USER)
    const { data } = useQuery(GET_ALL_USER)

    const deleteUser = (name: string, password: string): void =>
    {
        // deleteThisUser({
        //     variables:
        //     {
        //         name: name,
        //         password: password,
        //     }
        // })
        console.log("test", name)
    }

    return (
        <div>
            {data && data.getAllUsers.map((user: any) => 
            {
                return (
                    <div key = {user.id}>
                        {user.name} / {user.username} 
                        &nbsp;
                        <Button size="md" colorScheme="red" onClick= {() => { deleteUser(user.name, user.password) }}>Delete</Button>
                    </div>
                )
            })}
        </div>
    )
}

export default ListOfUser
